var UTIL = require( "util" );


module.exports.error = function( format ) {
	var text = UTIL.format.apply( this, arguments );
	var now = new Date().toString();

	console.error( now + " [ERROR] " + text );
};

module.exports.info = function( format ) {
	var text = UTIL.format.apply( this, arguments );
	var now = new Date().toString();

	console.error( now + " [INFO] " + text );
};
